class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> diff(n,0);
        int c=0;
        for(int i=1;i<n;i++)
        {
            if(colors[i]!=colors[i-1])
            c++;
            diff[i]=c;
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            int nxt=i+k-1;
            int no=0;
            if(nxt<n)
            {
                no=diff[nxt]-diff[i];
            }
            else
            {
                no=diff[n-1]-diff[i]+diff[nxt%n]-diff[0]+(colors[0]!=colors[n-1]);

            }
            if(no==k-1)
                ans++;
           // cout<<i<<" "<<nxt<<" "<<no<<" "<<ans<<endl;
        }
        return ans;
    }
};
