class Solution {
     public int numberOfAlternatingGroups(int[] colors, int k) {
         if (k == 1) {
             return colors.length;
         }

         int res = 0;
         int n = colors.length;
         int[] arr = new int[n + k - 1];
         for (int i = 0; i < n + k - 1; i++) arr[i] = colors[i % n];

         int s0 = 0, s1 = 0;
         if (colors[0] == 0) {
             s0 = 1; s1 = 0;
         } else {
             s0 = 0; s1 = 1;
         }

         for (int i = 1; i < n + k - 1; i++) {
             if (arr[i] == 0) {
                 s0 = s1 + 1;
                 s1 = 0;
             } else {
                 s1 = s0 + 1;
                 s0 = 0;
             }
             if (s1 >= k) res += 1;
             if (s0 >= k) res += 1;
         }
         return res;
     }
 }
