class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for(int i=0; i<n ;i++)
            a.push_back(a[i]);
        vector<int>DP(a.size());
        DP[0] = 1;
        for(int i=1; i<a.size(); i++) {
            if(a[i-1] == !a[i]) {
                DP[i] = DP[i-1]+1;
            }
            else {
                DP[i] = 1;
            }
        }
        int ans = 0;
    //     for(int i=0; i<= n-1 + k-1; i++)
    //          cout<<DP[i]<<" ";
    // cout<<endl;
        for(int i=n-1 + k-1; i >=k-1;) {
            int len = DP[i];
            if(DP[i]>=k)
                ans++;
            i--;
        }
        return ans;
    }
};
