class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors + [colors[0]]
        pre = [0] * (n*2)
        c = 1
        for i in range(n*2):
            pre[i] = c
            if colors[i] == colors[i+1]:
                c += 1
        res = 0
        for i in range(n):
            if pre[i] == pre[i+k-1]:
                res += 1
        return res
