class Solution {
    public int numberOfAlternatingGroups(int[] c, int k) {
        int n = c.length;
        int[] l = new int[n];
        l[0] = 1;
        for(int i = 1; i < n; i++){
            if(c[i]!=c[i-1]) l[i] = l[i-1]+1;
            else l[i] = 1;
            // System.out.println(i+":"+l[i]);
        }
        int ret = 0;
        for(int i = 0; i < n; i++){
            /**
            [i-k+1, i]
            */
            if(i-k+1>=0){
                if(l[i]>=k)ret++;
            }else{
                int q = l[i];
                if(q != i+1) continue;
                if(c[0]==c[n-1]) continue;
                if(q+l[n-1]>=k) ret++;
            }
        }
        return ret;
    }
}
