class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0;i<k-1;i++){
            v.push_back(v[i]);
        }
        int i=0,j=1;
        int n = v.size();
        v.push_back(v[n-1]);
        n++;
        int ans = 0;
        while(i<n && j<n){
            if((j-i) == k){
                ans++;
                // cout<<i<<" "<<j<<endl;
                i++;
                continue;
            }
            if(v[j] != v[j-1]){
                j++;
                continue;
            }
            i = j;
            j++;

        }
        return ans;
    }
};
