class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int answer=0;
        vector<int> v=colors;
        int n=v.size();
        for(int i:colors){
            v.push_back(i);
        }
        vector<int> F(n,0);
        int c=1;
        for(int i=n-1;i<2*n-1;i++){
            if(v[i]!=v[i+1]){
               c++;
            }else break;
        }
        F[n-1]=c;
        for(int i= n-2;i>=0;i--){
            if(v[i]!=v[i+1]){
                F[i]=1+F[i+1];
            }else {
                F[i]=1;
            }
        }
        for(int i=0;i<n;i++){
            answer+=(F[i]>=k);
        }
        return answer;

    }
};
