class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>f = colors; f.insert(f.end(), colors.begin(), colors.end());
        int ans = 0;
        set<int>bad;
        for (int i = 0; i < k-1; i++) {
            if (f[i] == f[i+1]) bad.insert(i);
        }
        if (bad.size() == 0) ans++;
        //cout << ans << endl;
        for (int i = 1; i < colors.size(); i++) {
            if (bad.find(i-1) != bad.end()) bad.erase(i-1);
            int idx = i + k - 2;
            if (f[idx] == f[idx+1]) bad.insert(idx);
            if (bad.size() == 0) ans++;
            //cout << i << "," << bad.size() << endl;
        }
        return ans;

    }
};
