class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ans = 0;
        vector<int>vis(n);
        for(int i = 0; i < n; i++) 
            if(colors[i] != colors[(i + 1) % n] && colors[i] != colors[(i - 1 + n) % n]) vis[i] = 1;
        for(int i = 0; i < n; i++) printf("%d ", vis[i]); puts("");
        int left = 0, right = 0, sum = 0;
        for(int i = 0; i < k; i++) sum += vis[i];
        right = k - 1;
        while(left < n) {
            if(sum == k) ans += 1;
            else if(vis[left] == 0 && vis[right] == 0 && sum == k - 2) ans += 1;
            else if(sum == k - 1) ans += 1;
            sum -= vis[left];
            left += 1;
            right = (right + 1) % n;
            sum += vis[right];
        }
        return ans;
    }
};
