class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& vi, int k) {
        int ans=0,sum=0,n;
        vector<int> vi1;
        for(int i=0;i<k-1;i++){
            vi.push_back(vi[i]);
        }
        n=vi.size();
        for(int i=0;i<n-1;i++){
            if(vi[i]!=vi[i+1]){
                vi1.push_back(1);
            }
            else{
                vi1.push_back(0);
            }
        }
        n=vi1.size();
        for(int i=0;i<vi1.size();i++){
            if(vi1[i]==1){
                sum+=vi1[i];
                if(sum>=k-1){
                    ans++;
                }
            }
            else{
                sum=0;
            }
        }
        return ans;
    }
};
