class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        for(int i = 0; i < k - 1; ++i) 
            a.push_back(a[i]);

        int n = a.size();
        vector<int> pref(n);
        for(int i = 1; i < n; ++i){
            pref[i] = pref[i - 1];
            if(a[i] != a[i - 1])
                ++pref[i];
        } 


        int res = 0;
        for(int i = k - 1; i < n; ++i){
            int x = pref[i] - pref[i - k + 1];
            if(x == k - 1)
                ++res;
        }
        return res;
    }
};
