class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans=0;
        vector<int> v1;

        //v1.push_back(v[v.size()-1]);
        for(int i=0;i<v.size();i++) v1.push_back(v[i]);
        for(int i=0;i<k-1;i++) v1.push_back(v[i]);
        //v1.push_back(v[0]);
        int n=v1.size();
        vector<int> dp(n+2,1);
        for(int i=1;i<n-1;i++){
            if(v1[i]!=v1[i-1]&&v1[i]!=v1[i+1]){dp[i]+=dp[i-1];}

        }
        for(int i=1;i<n-1;i++){
            if(dp[i]>=k-1) ans++;
        }
        return ans;

    }
};
