class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>a;
        for(int nxt: colors){
            a.push_back(nxt);
        }
        for(int nxt: colors){
            a.push_back(nxt);
        }
        int n = colors.size();
        int ans = 0;
        vector<int>d(2*n);
        for(int i = 1; i<2*n; i++){
            d[i] = (a[i]^a[i-1]);
            d[i]+=d[i-1];
        }

        for(int i = k-1; i<n+k-1; i++){
            int v = d[i];
                v-=d[i-(k-1)];
            if(v==k-1)ans++;
        }
        return ans;
    }
};
