class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        vector<int> temp(2*n, 1);
        for(int i = 1; i < n; i++){
            if(arr[i] == arr[i - 1]){
                temp[i] = 0;
            }
        }
        if(arr[0] == arr[n - 1]){
            temp[n] = 0;
        }
        for(int i = 1; i < n; i++){
            if(arr[i] == arr[i - 1]){
                temp[i + n] = 0;
            }
        }
        for(int i = 1; i < 2*n; i++){
            temp[i] += temp[i-1];
        }
        int ans = 0;
        for(int i = 0; i < n; i++){
            if(temp[i+k-1] - temp[i] == k - 1){
                ans++;
            }
        }
        return ans;
    }
};
