class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int value = 0, result = 0;
    vector<int> Arr = colors;
    deque<int> tmp;
    Arr.insert(Arr.end(), Arr.begin(), Arr.end());
    tmp.push_back(Arr[0]);
    for (int i = Arr.size() - 2; i >= 0; i -= 1) {
      if (tmp.back() == Arr[i]) {
        value += 1;
      }
      tmp.push_back(Arr[i]);
      while (tmp.size() > k) {
        int val = tmp.front();
        tmp.pop_front();
        value -= (val == tmp.front());
      }
      if (i < colors.size() && value == 0) {
        result += 1;
      }
    }
    return result;
  }
};
