#define sq(x) (x)*(x)
template<class T, class U> inline void chmin(T& a, U b) { if (a > b) a = b; }
template<class T, class U> inline void chmax(T& a, U b) { if (a < b) a = b; }
typedef long long ll;
typedef long double ld;
typedef __int128 i128;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = sz(a);
        rep(i, 0, n - 1) a.push_back(a[i]);

        vector<int> b(2*n);
        rep(i, 0, 2*n - 2) {
            b[i] = a[i] != a[i + 1];
            if (i) b[i] += b[i - 1];
        }
        int ans = 0;
        rep(i, k - 2, k - 2 + n) {
            int cur = (b[i] - (i >= k - 1 ? b[i - (k - 1)] : 0));
            if (cur == k - 1) ans++;
        }
        return ans;
    }
};
