class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        for (int i = 1; i < k; ++i) {
            c.push_back(c[i - 1]);
        }
        for (int i = c.size() - 1; i; --i) {
            c[i] ^= c[i - 1];
        }
        int ret = 0;
        for (int i = 1, j; (j = i) < c.size(); i = j) {
            while (j < c.size() && c[i] == c[j]) {
                ++j;
            }
            if (c[i] && j - i >= k - 1) {
                ret += j - i - k + 2;
            }
        }
        return ret;
    }
};
