class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        n = len(colors)
        colors = colors + colors
        ans = 0
        arr = []
        for i in range(n+k-1):
            arr.append(colors[i])
            if len(arr) >= k and arr[-1] != arr[-2]:
                ans += 1
            if len(arr) >= 2 and arr[-1] == arr[-2]:
                arr = arr[-1:]

        return ans
