class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int ct=0,ans=0,j=k;
        for(int i=1; i<k; i++) if(a[i]==a[i-1]) ct++;
        if(!ct) ans++;
        for(int i=0; i<n-1; i++,j++){
            if(a[j%n]==a[((j-1)%n+n)%n]) ct++;
            if(a[i]==a[(i+1)%n]) ct--;
            if(ct==0) ans++;
        }
        return ans;
    }
};
