from typing import List


class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        diff = []
        n = len(colors)
        diff.append(colors[0] != colors[-1])
        for i in range(1, n):
            diff.append(colors[i] != colors[i - 1])
        ans = 0
        cnt = 0
        if diff[0]:
            cnt = 1
        for i in range(1, k - 1):
            if cnt == i:
                if diff[-i]:
                    cnt += 1
                else:
                    break
        if cnt == k - 1:
            ans += 1
        for i in range(1, n):
            if diff[i]:
                cnt += 1
            else:
                cnt = 0
            if cnt >= k - 1:
                ans += 1
        return ans
