class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        int t = n;

        for(int i=0;i<n;i++)
        {
            arr.push_back(arr[i]);
        }

        n = arr.size();

        vector<int>rcnt(n,1);

        for(int i=n-2;i>=0;i--)
        {
            if(arr[i+1]!=arr[i])
            {
                rcnt[i]=rcnt[i+1]+1;
            }
            else
            {
                rcnt[i]=1;
            }
        }

        int ans = 0;

        for(int i=0;i<t;i++)
        {
            if(rcnt[i]>=k) ans++;
        }

        return ans;
    }
};
