#define pb(a) push_back(a)
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int dep=-1;
        for(int i=0;i<n;i++){
            if(a[i]==a[(i+1)%n]){
                dep=i;
                break;
            }
        }
        if(dep==-1){
            return n;
        }
        vector<int> v;
        v.pb(1);
        for(int i=dep;i<n+dep;i++){
            if(a[i%n]!=a[(n+i+1)%n]){
                v.back()++;
            }else v.pb(1);
        }
        int ans=0;
        for(auto i:v){
            cout<<i<<endl;
            if(i>=k)ans+=i-k+1;
        }
        return ans;
    }
};
