class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), l = 0, r = 1, p = 0, res = 0;
        while (r < k) {
            if (colors[r] == colors[r - 1]) {
                p = r;
            }
            ++r;
        }
        while (r < n + k) {
            if (l >= p) {
                ++res;
            }

            if (colors[r % n] == colors[(r - 1) % n]) {
                p = r;
            }
            ++r; ++l;
        }
        return res;
    }
};
