class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> ans;
        for(auto res : colors) {
        ans.push_back(res);
        }
        for(auto i = 0; i < k - 1; i++){
             ans.push_back(colors[i]);
        }
        int res = 0;
        for(auto i = 0; i < ans.size(); i++) {
            int counter = 1;
            for(auto j = i + 1; j < ans.size(); j++) {
                if(ans[j] != ans[j - 1]) 
                {
                    counter++;
                }
                else {
                    i = j - 1;
                    break;
                }
                if(j == ans.size() - 1) {
                    i = ans.size()*3;
                    break;
                }
            }
            int maxi=max(0, counter - k + 1);
            res += maxi;
        }
        return res;
    }
};
