class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size = colors.size();
        int cur = 1;
        int res = 0;
        for(int i=1;i<k;i++)
        {
            if(colors[i]+colors[i-1]==1)
            {
                cur++;
            }
            else
            {
                cur = 1;
            }
        }
        if(cur == k) res++;
        for(int i=1;i<size;i++)
        {
            if(colors[(i+k+size-1)%size]+colors[(i+k+size-2)%size]==1)
            {
                cur++;
                if(cur>=k)
                {
                    cur = k;
                    res++;
                }   
            }
            else
            {
                cur = 1;
            }
        }
        return res;
    }
};
