class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors *= 2
        lis = []
        for i in range(len(colors)-1):
            if colors[i] != colors[i+1]:
                lis.append(1)
            else:
                lis.append(0)
        c = 0
        ans = 0
        print(lis)
        for i in range(k-1):
            c += lis[i]
        if c == k - 1:
            ans += 1
        for i in range(1, n):
            c += lis[i+(k-1)-1]
            c -= lis[i-1]
            if c == k - 1:
                print(i)
                ans += 1
        return ans
