class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k - 1; i++) {
            colors.push_back(colors[i]);
        }
        int n = colors.size(), ans = 0, c = 1, prev = colors[0];
        for (int i = 1; i < n; i++) {
            if (colors[i] != prev) {
                c++;
            } else {
                ans += max(0, c - k + 1);
                c = 1;
            }
            prev = colors[i];
        }
        ans += max(0, c - k + 1);
        return ans;
    }
};
