/**
 * @param {number[]} colors
 * @param {number} k
 * @return {number}
 */
var numberOfAlternatingGroups = function(C, k) {
    C.push(...C.slice(0,k-1));
    var R = 0, N = 1;
    for(var i = 1; i < C.length; ++i){
        C[i] != C[i-1] ? ++N : (N=1);
        N >= k && ++R;
    }
    return R;
};
