class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> vv=colors;
        int n=colors.size();
        for(auto x:colors)  vv.push_back(x);
        vector<int> prefix(n*2);
        for(int i=0;i<vv.size();i++){
            prefix[i]=vv[i]+(i-2>=0 ? prefix[i-2] : 0);
        }
        int res=0;
        for(int i=0;i<n;i++){
            if(k&1){
                int num1=prefix[i+k-1]-prefix[i]+vv[i];
                int num2=prefix[i+k-2]-prefix[i+1]+vv[i+1];
                if(num1==((k+1)/2) && num2==0)  res++;
                if(num2==(k/2) && num1==0)  res++;
            }
            else{
                int num1=prefix[i+k-1]-prefix[i+1]+vv[i+1];
                int num2=prefix[i+k-2]-prefix[i]+vv[i];
                if(num1==(k/2) && num2==0)  res++;
                if(num2==(k/2) && num1==0)  res++;
            }
        }
        return res;
    }
};
