class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        res = 0
        cur_length = 0
        last = -1

        for char in colors + colors[:k-1]:
            if char == last:
                cur_length = 1
            else:
                cur_length += 1
                last = char
                if cur_length == k:
                    res += 1
                    cur_length -= 1

        return res
