class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> cnt(2 * n);
        for (int i = 1; i < 2 * n; i++) {
            cnt[i] = cnt[i - 1];
            cnt[i] += colors[i % n] == colors[(i - 1) % n];
        }
        int ans = 0;
        for (int i = k - 1; i < n + k - 1; i++) {
            ans += cnt[i] - cnt[i - k + 1] == 0;
        }
        return ans;
    }
};
