class Solution {
public:
int totalGroups = 0;

    void countGroups(const vector<int>& sequence, int targetLength, int size, int position, int currentLength) {
        if (position > size + targetLength) return;

        if (currentLength >= targetLength) totalGroups++;

        if (sequence[position % size] != sequence[(position - 1) % size]) {
            countGroups(sequence, targetLength, size, position + 1, currentLength + 1);
        } else {
            countGroups(sequence, targetLength, size, position + 1, 1);
        }
    }

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int size = colors.size();
        countGroups(colors, k, size, 1, 0);
        return totalGroups;
    }
};
