class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = (int) colors.size(), res = 0;
        for(int it = 0; it < 2; it++) {
            vector<int> ps(2 * n + 1, 0);
            for(int i = 0; i < 2 * n; i++) {
                ps[i + 1] = ps[i] + (colors[i % n] == (i & 1));
            }
            for(int i = 0; i < n; i++) {
                if(ps[i + k] - ps[i] == k) ++res;
            }
            for(int i = 0; i < n; i++) colors[i] = 1 - colors[i];
        }
        return res;
    }
};
