class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for (int i = 0; i + 1 < k; i++) v.push_back(v[i]);
        int streak = 0, last = v[0];
        int ans = 0;
        for (int i : v) {
            if (last == i) streak = 1;
            else last ^= 1, ++streak;
            if (streak >= k) ++ans;
        }
        return ans;
    }
};
