class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n=(int)arr.size();
        vector<int> pre(n,0);
        for(int i=0;i<n;i++){
            int l=(i-1+n)%n;
            int h=(i+1)%n;
            if(arr[l]==arr[h] && arr[l]!=arr[i]){
                pre[i]=1;
            }
            if(i> 0){
                pre[i]+=pre[i-1];
            }
        }
        int fin=0;
        for(int i=0;i<n;i++){
            int j=(i+k-1);
            if(j-1<n){
                int temp=pre[j-1]-pre[i];
                if(temp==k-2){
                    fin ++ ; 
                }
            }
            else{
                int x=k-(n-i);
                int idx=x-2;
                int temp=pre[n-1]-pre[i]+pre[idx];
                if(temp==k-2){
                    fin ++ ; 
                }
            }
        }
        return fin;
    }
};
