class Solution:
    def numberOfAlternatingGroups(self, cs: List[int], k: int) -> int:
        ans = 0
        cs = cs + cs[:k-1]
        n = len(cs)
        ans = 0
        i = 0
        while i < n:
            if i + k  >= n + 1 : break
            t = 1
            for j in range(i, n):
                if j + 1 >= n: return ans
                if cs[j] != cs[j+1]:
                    t += 1
                    if t >= k:
                        ans += 1
                else:
                    i = j + 1
                    t = 1
                    break
        return ans
