class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int m) {
        int n = c.size();
        c.push_back(c[0]);
        bool ok = true;
        for(int k=0;k<n;k++) {
            if (c[k] == c[k + 1]) ok = false;
        }
        if (ok) {
            return n;
        }
        vector<int> p;
        int cnt = 1;
        for(int k=0;k<n;k++) {
            if (c[k] == c[k + 1]) {
                p.push_back(cnt);
                cnt = 1;
            } else {
                if (k == n - 1) {
                    p[0] += cnt;
                }
                cnt++;
            }
        }
        int ans = 0;
        for(auto a: p) {
            if (a >= m) ans += a - m + 1;
        }
        return ans;
    }
};
