class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors = colors + colors
        j = n - 1
        x = colors[n] ^ 1
        while j >= 0 and colors[j] == x:
            x ^= 1
            j -= 1
        # print("n",n,"j",j)
        ans = 1 if n - j >= k else 0
        for i in range(n + 1, len(colors)):
            if colors[i] == colors[i - 1]:
                j = i - 1
            ans += 1 if i - j >= k else 0
        return ans
