class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

       auto temp=colors;
       for(int i=0;i<k-1;i++)temp.push_back(colors[i]);
       int n=temp.size(),i=0,j=1;
        // for(int i=0;i<n;i++)cout<<temp[i]<<" ";
        // cout<<endl;
        int flag=0;
       int count=0;
       while(j<n){
           if(j-i+1<k){
               if((temp[j]==temp[j-1])||(temp[j]==temp[j+1])){
                   i=j;
               }
           }
           else{
               if(flag==0){
                   flag=1;
                   count++;
                   i++;
               }
               else
               {
               if((temp[j-1]==temp[j-2])||(temp[j]==temp[j-1])){
                   i=j;
               }
               else{
                   count++;
                   i++;
               }
               }
           }
            j++;
        }

       return count;
    }
};
