class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        int n = colors.size();
        vector<int> v(2*n,0);
        for(int i=1;i<n-1;i++)
        {
            if(colors[i] != colors[i-1] && colors[i] != colors[i+1])
            v[i]=1,v[i+n]=1;
        }
        if(colors[0] != colors[1] && colors[0] != colors[n-1]) v[0]=1,v[n]=1;
        if(colors[n-2] != colors[n-1] && colors[0] != colors[n-1]) v[n-1]=1,v[2*n-1]=1;
        int left=0,right=0,count=0,sum=0;
        for(int i=0;i<k-2;i++)
        sum += v[i];
        for(int i=0;i<n;i++)
        {
            if(sum==k-2) count++;
            sum -= v[i];
            sum += v[i+k-2];
        }
        return count;

    }
};
