class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        ans = 0
        colors.extend(colors[:k - 1])
        i = 1
        while i < len(colors) - 1:
            if not (colors[i] != colors[i - 1] and colors[i] != colors[i + 1]):
                i += 1
                continue
            i0 = i
            while i < len(colors) - 1 and colors[i] != colors[i - 1] and colors[i] != colors[i + 1]:
                i += 1
            l = i - i0 + 2
            ans += max(0, l - k + 1)
        return ans
