class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        curr = 1
        ans = 0
        for i in range(1, n + k - 1):
            if colors[i % n] != colors[(i - 1) % n]:
                curr += 1
            else:
                ans += max(0, 1 + curr - k)
                curr = 1
                if i >= n:
                    break
        ans += max(0, 1 + curr - k)
        return ans
