class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        xors = [colors[i % n] ^ colors[(i + 1) % n] for i in range(n + k - 2)]

        ret = 0
        acc = 0
        for x in xors:
            if x == 1:
                acc += 1
                if acc >= k - 1:
                    ret += 1
            else:
                acc = 0

        return ret
