class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k > n) return 0;

        int cnt = 0;
        int ac = 1;

        for (int i = 1; i < n + k - 1; ++i) {
            if (colors[i % n] != colors[(i - 1) % n]) {
                ac++;
            } else {
                ac = 1;
            }

            if (ac == k) {
                cnt++;
                ac--;
            }
        }

        return cnt;
    }
};
