class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v,int k){
        int ans=0,n=v.size();
        for(int i=0;i<n;i++) v.push_back(v[i]);
        // for(auto i:v) cout<<i<<" "; cout<<'\n';
        int id=0,col=v[0];
        for(int i=1;i<n+k-1;i++){
            // cout<<i<<" "<<id<<" "<<col<<" "<<ans<<"   ";
            if(col!=v[i]){
                if((i-id+1)==k){
                    ans++; id++;
                }
            }
            else{
                id=i;
            }
            col=v[i];
            // cout<<id<<" "<<col<<" "<<ans<<'\n';
        }
        return ans;
    }
};
