class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        l0=len(colors)
        colors+=colors[:k-1]
        l1=len(colors)
        hl=[1]*l1
        curr=0-colors[-1]
        con=0

        for i in range(l1-1,-1,-1):
            if colors[i]!=curr:
                con+=1
                hl[i]=con
            else:
                con=1
            curr=colors[i]

        ans=0
        for i in hl[:l0]:
            if i>=k:
                ans+=1
        return ans
