class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size(),ans=0;
        vector<int>f(n+1,0);
        f[1]=1;
        for(int i=2;i<=n;i++)
        {
            if(nums[i-1]==nums[i-2])f[i]=1;
            else f[i]=f[i-1]+1;
        }

        // for(int i=1;i<=n;i++)cout<<f[i]<<' ';
        // cout<<'\n';
        for(int i=1;i<=n;i++)
        {
            if(i+k-1<=n){
                if(f[i+k-1]-f[i]+1==k)ans++;
            }else {
                if(nums[0]==nums[n-1])continue;
                int v1=f[n]-f[i]+1,v2=f[k-(n-i+1)];
                if(v1+v2==k)ans++;
            }
        }
        return ans;
    }
};
