class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
         int n = colors.size(); 
        for(int i = 0; i < n; i++){ 
            colors.push_back(colors[i]); 
        } 
        int low=1;
        int high=0;
        int ans=0;
        while(high < n && low - k < n){ 
            if(colors[low]!=colors[low-1]){ 
                low++; 
            } 
            else{ 
                high=low;
                low++;
            } 
            int cnt=0;
            int diff=low-high;
            if(diff>=k){ 
                high++;
                cnt++;
            } 
            ans+=cnt;
        } 
        return ans; 
    }
};
