class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        auto arr = colors;
        for (auto x:colors) {
            arr.push_back(x);
        }
        int n = colors.size();
        vector <int> dp(n+n);
        for (int i=0; i<n+n; ++i) {
            dp[i] = 1;
            if (i && arr[i-1] != arr[i]) {
                dp[i] = 1 + dp[i-1];
            }
        }
        int ans = 0;
        for (int i=n; i<n+n; ++i) {
            if (dp[i] >= k) {
                ans += 1;
            }
        }
        return ans;

    }
};
