class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for (int i=0;i<n;i++){
            colors.emplace_back(colors[i]);
        }
        int sum=0;
        int now = colors[0];
        int num=1;
        for (int i=1;i<(n+k);i++){
            //cout<<"i="<<colors[i]<<" "<<now<<" "<<num<<endl;
            if (colors[i]!=now){
                num++;
                now=colors[i];
            } else{
                //cout<<"i="<<i<<endl;
                sum+=max(0,num-k+1);
                num=1;
                now=colors[i];
            }
        }
        sum+=max(0,num-k);
        return sum;
    }
};
